$(document).ready(function(){$(".readmore__parent").each(function(){var e=$(this).html();if(e.length>800){var s='<div class="readmore-text" style="display:block">'+e.substr(0,800)+'<span class="moreellipses">...&nbsp;&nbsp;<a rel="nofollow" href="#" class="moreless more">Читать весь комментарий...</a></span></span></div><div class="readmore-text" style="display:none">'+e+'<a rel="nofollow" href="#" class="moreless less">Скрыть комментарий</a></span></div>';$(this).html(s)}}),$(".moreless").click(function(){var e=$(this),s=e.closest(".readmore-text"),l=".readmore-text";return e.hasClass("less")?(s.prev(l).toggle(),s.slideToggle()):(s.toggle(),s.next(l).fadeToggle()),!1})}),$(document).ready(function(){$(".less").click(function(){$("html, body").animate({scrollTop:$(this).closest(".readmore__parent").offset().top-80+"px"},400)})});